import React from 'react'
import { useEffect, useState, useContext } from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
import img from '../../Assets/default-profile.svg'

import ProtectedRoute from '../../utils/ProtectedRoute'

const Profile = ({ history }) => {
  // const [user, setUser] = useState({})
  // let logedInUser = {}

  // useEffect(() => {
  //   logedInUser = JSON.parse(localStorage.getItem('logedInUser'))
  //   setUser(logedInUser)
  // }, [])

  // console.log(user)

  const [name, setName] = useState(
    JSON.parse(localStorage.getItem('logedInUser')).name
  )
  const [username, setUsername] = useState(
    JSON.parse(localStorage.getItem('logedInUser')).username
  )
  const [email, setEmail] = useState(
    JSON.parse(localStorage.getItem('logedInUser')).email
  )
  // console.log(name)
  // console.log(username)
  // console.log(email)
  // const [password, setPassword] = useState('')

  // console.log(user)

  return (
    <Wrapper>
      <h1 className='heading'>Profile</h1>
      <div className='profile'>
        <div>
          <img className='profile_pfp' src={img} alt='' />
          <button className='button-to-update-pfp'>Update PFP</button>
        </div>
        <form>
          {/* For Name */}
          <input
            type='text'
            id='name'
            autoComplete='off'
            onChange={(e) => {
              setName(e.target.value)
            }}
            value={name}
            placeholder={name}
          />

          <input
            type='text'
            id='username'
            autoComplete='off'
            onChange={(e) => {
              setUsername(e.target.value)
            }}
            value={username}
            placeholder={username}
          />
          {/* For Email */}
          <input
            type='email'
            id='email'
            autoComplete='off'
            onChange={(e) => {
              setEmail(e.target.value)
            }}
            value={email}
            placeholder={email}
          />

          {/* <button>
            <Link to='/'>Change Password</Link>
          </button> */}
          <Link to='/profile/resetPassword'>
            <button>Change password</button>
          </Link>
        </form>
      </div>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  max-width: 900px;
  /* height: 800px; */
  margin: 0 auto;
  background-color: #fcfcfc;
  padding: 20px 50px 40px;
  box-shadow: 1px 4px 10px 1px #aaa;
  font-family: sans-serif;

  .heading {
    margin-left: 20rem;
    margin-bottom: 10rem;
  }

  .profile {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
  }
  .button-to-update-pfp {
    margin-left: 5.5rem;
  }

  .profile_pfp {
    margin-bottom: 10rem;
    margin-left: 3rem;
    border-radius: 50%;
    background-color: var(--buttonColor);
  }

  img {
    /* font-size: 100px; */
    width: 200px;
    height: 200px;
    margin-left: 4rem;
    /* background: black; */
  }

  * {
    box-sizing: border-box;
  }

  h2 {
    text-align: center;
    margin-bottom: 30px;
  }

  input {
    margin-bottom: 15px;
  }

  input {
    display: block;
    height: 32px;
    padding: 6px 16px;
    width: 100%;
    border: none;
    background-color: #f3f3f3;
  }
  label {
    color: #777;
    font-size: 0.8em;
  }

  button {
    display: block;
    /* margin: 20px auto 0; */
    width: 150px;
    height: 40px;
    border-radius: 25px;
    border: none;
    color: #eee;
    font-weight: 700;
    font-size: 15px;
    box-shadow: 1px 4px 10px 1px #aaa;

    background: var(--buttonColor);
  }
  button:hover {
    background: black;
  }
`

export default ProtectedRoute(Profile)
