import React from 'react'
import { useEffect, useState } from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom'

const Register = ({ history }) => {
  const [discription, setDiscription] = useState('')
  const [amount, setAmount] = useState(0)
  const [paidBy, setPaidBy] = useState(
    JSON.parse(localStorage.getItem('current-user')).user.username
  )
  const [validConfirmPassword, setValidConfirmPassword] = useState(false)
  const [username, setUsername] = useState('')
  const [email, setEmail] = useState('')

  const handleSubmit = async (e) => {
    e.preventDefault()
    console.log('Expensese Added')
    var options = document.getElementById('Bikes').selectedOptions
    console.log(options[0])
  }

  return (
    <Wrapper>
      <h1>Enter New expense</h1>
      <div>
        <form onSubmit={handleSubmit}>
          {/* For Name */}
          <input
            type='text'
            id='discription'
            onChange={(e) => {
              setDiscription(e.target.value)
            }}
            value={discription}
            required
            placeholder='Discription'
          />
          {/* For Username */}
          <input
            type='number'
            id='amount'
            onChange={(e) => {
              setAmount(e.target.value)
            }}
            value={amount}
            required
            placeholder='Amount'
          />
          {/* For Email */}
          <input
            type='text'
            id='paidBy'
            onChange={(e) => {
              setPaidBy(e.target.value)
            }}
            value={paidBy}
            required
            placeholder='Paid By'
          />

          <select id='Bikes' multiple>
            <option value='HeroHonda'>HeroHonda</option>
            <option value='Splender'>Splender</option>
            <option value='Ninja'>Ninja</option>
            <option value='Pulsav'>Pulsav</option>
          </select>

          <button>Add Expenses</button>
        </form>
      </div>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  width: 600px;
  /* height: 800px; */
  margin: 0 auto;
  background-color: #fcfcfc;
  padding: 20px 50px 40px;
  box-shadow: 1px 4px 10px 1px #aaa;
  font-family: sans-serif;

  * {
    box-sizing: border-box;
  }

  h2 {
    text-align: center;
    margin-bottom: 30px;
  }

  input {
    margin-bottom: 15px;
  }

  input {
    display: block;
    height: 32px;
    padding: 6px 16px;
    width: 100%;
    border: none;
    background-color: #f3f3f3;
  }
  label {
    color: #777;
    font-size: 0.8em;
  }

  button {
    display: block;
    margin: 20px auto 0;
    width: 150px;
    height: 40px;
    border-radius: 25px;
    border: none;
    color: #eee;
    font-weight: 700;
    font-size: 15px;
    box-shadow: 1px 4px 10px 1px #aaa;

    background: var(--buttonColor);
  }
  button:hover {
    background: black;
  }
`

export default Register
