import { createSlice } from '@reduxjs/toolkit'
import { GET_ALL_TRANSACTION } from '../../utils/constant'

const initialState = {
  // transactions: !localStorage.getItem(GET_ALL_TRANSACTION)
  //   ? []
  //   : JSON.parse(localStorage.getItem(GET_ALL_TRANSACTION)),
  transactions: [],
}

const transactionSlice = createSlice({
  name: 'transactions',
  initialState,
  reducers: {
    addExpenses: (state, { payload }) => {
      state.transactions.push(payload)
      console.log(payload)
    },
    emptyState: (state) => {
      state.transactions = []
    },
  },
})
export const { addExpenses, emptyState } = transactionSlice.actions
export default transactionSlice.reducer
