import React from 'react'
import { useEffect, useState, useContext } from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom'

import axios from '../api/axios'
import AuthContext from '../context/AuthProvider'

let VERIFY_URL = '/forgetPassword?email='

const ForgotPassword = ({ history }) => {
  const [email, setEmail] = useState('')
  const [errMsg, setErrMsg] = useState('')
  const [success, setSuccess] = useState(false)
  useEffect(() => {
    setErrMsg('')
  }, [email])

  const handleSubmit = async (e) => {
    e.preventDefault()

    try {
      const response = await axios.post(
        VERIFY_URL + email,
        JSON.stringify({
          url: window.location.href,
        }),
        {
          headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*',
          },

          withCredentials: false,
        }
      )
      // console.log(window.location.href)
      // console.log(response.data.message)
      alert(response.data.message)
      setEmail('')
      // history.push('/forgetpassword/verifyotp')
    } catch (error) {
      setErrMsg('Invalid Entry')
      alert(error)
    }
  }

  return (
    <Wrapper>
      <h1>Reset your Password</h1>
      <p>
        Enter email to reset your password. An OTP will be shared with your
        email and you will be redirected to enter your new password
      </p>
      <div>
        <form onSubmit={handleSubmit}>
          {/* For Email */}
          <input
            type='email'
            id='email'
            autoComplete='off'
            onChange={(e) => {
              setEmail(e.target.value)
            }}
            value={email}
            required
            placeholder='Email'
          />

          <button disabled={!email ? true : false}>Send OTP</button>
        </form>
      </div>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  width: 600px;
  /* height: 800px; */
  margin: 0 auto;
  background-color: #fcfcfc;
  padding: 20px 50px 40px;
  box-shadow: 1px 4px 10px 1px #aaa;
  font-family: sans-serif;

  * {
    box-sizing: border-box;
  }

  h2 {
    text-align: center;
    margin-bottom: 30px;
  }

  input {
    margin-bottom: 15px;
  }

  input {
    display: block;
    height: 32px;
    padding: 6px 16px;
    width: 100%;
    border: none;
    background-color: #f3f3f3;
  }
  label {
    color: #777;
    font-size: 0.8em;
  }

  button {
    display: block;
    margin: 20px auto 0;
    width: 150px;
    height: 40px;
    border-radius: 25px;
    border: none;
    color: #eee;
    font-weight: 700;
    font-size: 15px;
    box-shadow: 1px 4px 10px 1px #aaa;

    background: var(--buttonColor);
  }
  button:hover {
    background: black;
  }
`

export default ForgotPassword
